import{_ as e,c as l,o as a,d as i}from"./app.ca45d9bb.js";const m=JSON.parse('{"title":"前端开发","description":"","frontmatter":{"title":"前端开发"},"headers":[{"level":2,"title":"科技树","slug":"科技树","link":"#科技树","children":[]},{"level":2,"title":"细节注意","slug":"细节注意","link":"#细节注意","children":[]},{"level":2,"title":"本地测试服务器设置","slug":"本地测试服务器设置","link":"#本地测试服务器设置","children":[]},{"level":2,"title":"学习网址推荐","slug":"学习网址推荐","link":"#学习网址推荐","children":[]},{"level":2,"title":"参考链接","slug":"参考链接","link":"#参考链接","children":[]}],"relativePath":"development/front-end/introduction.md","lastUpdated":1674218161000}'),t={name:"development/front-end/introduction.md"},n=i(`<h1 id="内容前瞻" tabindex="-1">内容前瞻 <a class="header-anchor" href="#内容前瞻" aria-hidden="true">#</a></h1><h2 id="科技树" tabindex="-1">科技树 <a class="header-anchor" href="#科技树" aria-hidden="true">#</a></h2><ul><li>HTML</li><li>CSS <ul><li>基础：语法、布局、构件样式应用等</li><li>进阶：预处理（Sass、Stylus、Less 等）</li><li>高阶：框架（Bootstrap、Element UI、Antd 等）、优化（Styled-Component、CSS Modules等）</li></ul></li><li>JavaScript <ul><li>基础：原生语法、数据类型、函数、对象、JSON等</li><li>进阶：异步（Ajax等）、JQuery、设计模式</li><li>高阶：框架（React、Vue、Angular 等）、ts</li></ul></li><li>服务器端编程：PHP、Nodejs、Ruby等</li><li>构建工具：webpack、Vite等</li><li>管理工具： <ul><li>版本管理：Git</li><li>软件包管理：npm/npx、yarn、pnpm</li></ul></li><li>数据管理： <ul><li>数据库：MySQL/PostgreSQL、MongoDB/CouchDB、Redis等</li><li>数据可视化：ECharts、AntV等</li></ul></li><li>性能优化：RAIL模型、骨架屏、懒加载预加载、防抖节流等</li><li>其他场域： <ul><li>桌面端：Electron</li><li>移动端：React Native等</li><li>小程序</li></ul></li></ul><h2 id="细节注意" tabindex="-1">细节注意 <a class="header-anchor" href="#细节注意" aria-hidden="true">#</a></h2><ol><li>命名规则建议：文件夹和文件名使用<strong>小写</strong>，用<strong>短横线</strong>来分隔。如：my-file.html。</li><li>文件引用规则：</li></ol><ul><li>若引用的目标文件与 HTML 文件<strong>同级</strong>，只需直接使用文件名。如：<code>my-image.jpg</code></li><li>要引用<strong>子目录</strong>中的文件，请在路径前面写上目录名，再加上一个正斜杠。如：<code>subdirectory/my-image.jpg</code></li><li>若引用的目标文件位于 HTML 文件的<strong>上级</strong>，需要加上两个点。如：若 <code>index.html</code> 在 <code>test-site</code> 的一个子文件夹内，而 <code>my-image.jpg</code> 在 <code>test-site</code> 内，你可以使用<code>../my-image.jpg</code> 从 <code>index.html</code> 引用 <code>my-image.jpg</code></li><li>以上方法可以随意组合</li></ul><ol start="3"><li>文件路径书写建议：使用<strong>正斜杠（/）</strong>。（尽管 Windows 的文件系统使用反斜杠而不是正斜杠）</li></ol><h2 id="本地测试服务器设置" tabindex="-1">本地测试服务器设置 <a class="header-anchor" href="#本地测试服务器设置" aria-hidden="true">#</a></h2><blockquote><p>一般情况下，只需要在浏览器中直接打开本地示例即可。 但某些示例若以本地文件方式打开可能不会运行，可能原因如下：</p><ul><li>具有异步请求：安全限制。——&gt; 可使用Python的相关模块，如下所示。</li><li>具有服务端代码：需要部署服务端来解释代码并提供结果。——&gt; 需搭建对应的本地服务端。</li></ul></blockquote><p>使用 Python 的 <code>SimpleHTTPServer</code> 模块。</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-palenight"><code><span class="line"><span style="color:#A6ACCD;"># 进入示例所在目录启动服务器 (Python 版本为 3.x)</span></span>
<span class="line"><span style="color:#A6ACCD;">python -m http.server</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>默认情况下会启用本地 Web 服务器的 8000 端口来运行，但也可更换其他端口。</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-palenight"><code><span class="line"><span style="color:#A6ACCD;"># 变更服务端口 (以 7800 端口为例)</span></span>
<span class="line"><span style="color:#A6ACCD;">python -m http.server 7800</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><h2 id="学习网址推荐" tabindex="-1">学习网址推荐 <a class="header-anchor" href="#学习网址推荐" aria-hidden="true">#</a></h2><p><a href="https://developer.mozilla.org/zh-CN/" target="_blank" rel="noreferrer">MDN：Web开发文档</a><br><a href="http://c.biancheng.net/" target="_blank" rel="noreferrer">C语言编程网：Web前端</a></p><h2 id="参考链接" tabindex="-1">参考链接 <a class="header-anchor" href="#参考链接" aria-hidden="true">#</a></h2><p><a href="https://zhuanlan.zhihu.com/p/115110546" target="_blank" rel="noreferrer">知乎：前端开发书籍推荐与学习路线（2022年全新推荐）</a><br><a href="https://zhuanlan.zhihu.com/p/164701269" target="_blank" rel="noreferrer">知乎：前端学习路线（很长，建议收藏）</a></p>`,17),s=[n];function r(o,c,d,p,h,u){return a(),l("div",null,s)}const _=e(t,[["render",r]]);export{m as __pageData,_ as default};
